class EvaluatorTest extends \PHPUnit_Framework_TestCase
{
    public function testReturningFirstValueWhenConditionTrue()
    {
        $expectedValue = 'val1';
        $actualValue = Evaluator::when(true)->then('val1')->otherwise('val2');
        $this->assertEquals($expectedValue, $actualValue);
    }

    public function testReturningSecondValueWhenConditionFalse()
    {
        $expectedValue = 'val2';
        $actualValue = Evaluator::when(false)->then('val1')->otherwise('val2');
        $this->assertEquals($expectedValue, $actualValue);
    }

    public function testReturningAValueWhenConditionTrueOtherwiseNull()
    {
        $this->assertNull(Evaluator::when(false)->thenReturn('val1'));

        $expectedValue = 'val1';
        $actualValue = Evaluator::when(true)->thenReturn('val1');
        $this->assertEquals($expectedValue, $actualValue);
    }

    public function testReturningConditionValueWhenNotFalseOrOtherwiseValue()
    {
        $expectedValue = 'val1';
        $actualValue = Evaluator::when('val1')->otherwise('val2');
        $this->assertEquals($expectedValue, $actualValue);
    }
}
